package Strings;

public class FindFirstSubstringOccurrence {
    public static void main(String[] args) {
      String s = "";
      String x = "";
      findFirstSubstringOccurrence(s,x);
    }
    static int findFirstSubstringOccurrence(String s, String x) {
        char[] cs =  s.toCharArray();
        char[] cx = x.toCharArray();
        if(cx.length>cs.length) return -1;

        long hashX=0, hashS=0;
        for(int  i=0 ; i<cx.length ; i++) {
            hashX+=cx[i];
            hashS+=cs[i];
        }
        for( int i=0 ; i<cs.length-cx.length+1 ; i++){
            int j=0;
            if(hashX==hashS){
                for( ; j<cx.length ; j++){
                    if(i+j>=cs.length) break;
                    if(cs[i+j]!=cx[j]) break;
                }
                if(j==cx.length) return i;
            }
            hashS -=cs[i];
            if(i+cx.length<cs.length)
                hashS +=cs[i+cx.length];

        }
        return -1;
    }
}
